SELECT d.city,d.county,xb.stat,max(log.operate_time) as "time" FROM `yzl_xb` xb left join yzl_log log on xb.county=log.dcode inner join yzl_district d on d.Anumber = xb.county WHERE xb.`stat` in
#{stat}
and xb.`COUNTY` in
#{authority}
group by county,xb.stat order by max(xb.stat) asc

